import React from "react";
import { MapContainer, TileLayer, Polyline, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

const startIcon = new L.Icon({
  iconUrl: "https://img.icons8.com/color/48/000000/marker.png",
  iconSize: [30, 30]
});
const endIcon = new L.Icon({
  iconUrl: "https://img.icons8.com/color/48/000000/marker--v1.png",
  iconSize: [30, 30]
});

function MapView({ routeData }) {
  if (!routeData || !routeData.route?.features?.length)
    return <div className="h-full w-full flex justify-center items-center text-gray-500">Enter route to display</div>;

  const coordinates = routeData.route.features[0].geometry.coordinates;
  const center = [coordinates[0][1], coordinates[0][0]];

  return (
    <MapContainer center={center} zoom={12} style={{ height: "100%", width: "100%" }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

      <Marker position={[coordinates[0][1], coordinates[0][0]]} icon={startIcon} />
      <Marker position={[coordinates[coordinates.length - 1][1], coordinates[coordinates.length - 1][0]]} icon={endIcon} />

      <Polyline positions={coordinates.map(c => [c[1], c[0]])} color="red" weight={5} />
    </MapContainer>
  );
}

export default MapView;
