import axios from "axios";

export const getRoute = async (start, end, mode) => {
  const res = await axios.post("http://localhost:5000/api/route", {
    start,
    end,
    mode
  });
  return res.data;
};

// Autocomplete places using ORS
export const autocompletePlace = async (query) => {
  try {
    const res = await axios.get(
      `https://api.openrouteservice.org/geocode/autocomplete?api_key=${process.env.REACT_APP_ORS_API_KEY}&text=${encodeURIComponent(query)}`
    );
    return res.data.features.map(f => f.properties.label);
  } catch (err) {
    console.error(err);
    return [];
  }
};
