import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import MapView from "./components/MapView";
import "./App.css";

function App() {
  const [routeData, setRouteData] = useState(null);
  const [loading, setLoading] = useState(false);

  return (
    <div className="app-container">
      {/* Sidebar */}
      <Sidebar setRouteData={setRouteData} setLoading={setLoading} />

      {/* Map Container */}
      <div className="map-container">
        {loading && <div className="loading-overlay">Fetching route...</div>}

        <MapView routeData={routeData} />

        {routeData && !loading && (
          <div className="info-panel">
            <div>Distance: {routeData.distanceKm?.toFixed(2)} km</div>
            <div>Carbon Emission: {routeData.emission} kg COâ‚‚</div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
