import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import MapView from "./components/MapView";

function App() {
  const [routeData, setRouteData] = useState(null);
  const [loading, setLoading] = useState(false);

  return (
    <div style={{
      display: "flex",
      flexDirection: "column",
      height: "100vh",
      fontFamily: "Arial, sans-serif"
    }}>

      {/* Sidebar / Controls */}
      <Sidebar setRouteData={setRouteData} setLoading={setLoading} />

      {/* Map Container */}
      <div style={{ flex: 1, position: "relative" }}>
        {loading && (
          <div style={{
            position: "absolute",
            top: 0, left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(255,255,255,0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
            fontWeight: "bold",
            fontSize: "1rem"
          }}>
            Fetching route...
          </div>
        )}

        <MapView routeData={routeData} />

        {/* Distance & Carbon Emission Panel */}
        {routeData && !loading && (
          <div style={{
            position: "absolute",
            bottom: 20,
            left: "50%",
            transform: "translateX(-50%)",
            backgroundColor: "#ffffffcc",
            padding: "12px 18px",
            borderRadius: 12,
            boxShadow: "0 2px 8px rgba(0,0,0,0.3)",
            fontSize: "0.95rem",
            zIndex: 1000,
            minWidth: "150px",
            textAlign: "center"
          }}>
            <div>Distance: {routeData.distanceKm?.toFixed(2)} km</div>
            <div>Carbon Emission: {routeData.emission} kg COâ‚‚</div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
