import React, { useState } from "react";
import { getRoute } from "../services/api";
import ModeSelector from "./ModeSelector";

function Sidebar({ setRouteData }) {
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [mode, setMode] = useState("driving-car");

  const fetchRoute = async () => {
    try {
      const data = await getRoute(start, end, mode);
      setRouteData(data);
    } catch (err) {
      alert("Failed to fetch route. Check console.");
    }
  };

  return (
    <div className="sidebar">
      <input
        type="text"
        placeholder="Start (lng,lat)"
        value={start}
        onChange={(e) => setStart(e.target.value)}
      />
      <input
        type="text"
        placeholder="End (lng,lat)"
        value={end}
        onChange={(e) => setEnd(e.target.value)}
      />

      <ModeSelector mode={mode} setMode={setMode} />

      <button onClick={fetchRoute}>Get Route</button>
    </div>
  );
}

export default Sidebar;
