import React from "react";
import { MapContainer, TileLayer, Polyline, Marker } from "react-leaflet";

function MapView({ routeData }) {
  if (!routeData || !routeData.route || !routeData.route.features || routeData.route.features.length === 0) {
    return <div className="map">Enter route to display</div>;
  }

  const coordinates = routeData.route.features[0].geometry.coordinates;
  const center = [coordinates[0][1], coordinates[0][0]]; // lat, lng

  return (
    <MapContainer center={center} zoom={12} style={{ height: "100vh", width: "100%" }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <Marker position={[coordinates[0][1], coordinates[0][0]]} />
      <Marker position={[coordinates[coordinates.length - 1][1], coordinates[coordinates.length - 1][0]]} />
      <Polyline positions={coordinates.map((c) => [c[1], c[0]])} color="red" />
    </MapContainer>
  );
}

export default MapView;
