// frontend/src/components/ModeSelector.js
import React from "react";

function ModeSelector({ mode, setMode }) {
  const modes = [
    { value: "driving-car", label: "Car üöó" },
    { value: "cycling-regular", label: "Bike üèçÔ∏è" },
    { value: "foot-walking", label: "Walk üö∂" },
    { value: "driving-hgv", label: "Bus üöå" } // using HGV (heavy vehicle) as bus
  ];

  return (
    <div className="mode-selector">
      <h3>Choose Mode:</h3>
      <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
        {modes.map((m) => (
          <button
            key={m.value}
            onClick={() => setMode(m.value)}
            style={{
              padding: "8px 14px",
              borderRadius: "8px",
              border: mode === m.value ? "2px solid #007bff" : "1px solid #ccc",
              background: mode === m.value ? "#007bff" : "#f9f9f9",
              color: mode === m.value ? "#fff" : "#000",
              cursor: "pointer",
              fontWeight: "bold"
            }}
          >
            {m.label}
          </button>
        ))}
      </div>
    </div>
  );
}

export default ModeSelector;
