import React, { useState } from "react";
import { getRoute, autocompletePlace } from "../services/api";
import ModeSelector from "./ModeSelector";
import "./Sidebar.css";

function Sidebar({ setRouteData, setLoading }) {
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [mode, setMode] = useState("driving-car");
  const [startSuggestions, setStartSuggestions] = useState([]);
  const [endSuggestions, setEndSuggestions] = useState([]);

  // Fetch suggestions for start
  const handleStartChange = async (e) => {
    const value = e.target.value;
    setStart(value);
    if (value.length > 2) {
      const suggestions = await autocompletePlace(value);
      setStartSuggestions(suggestions);
    } else {
      setStartSuggestions([]);
    }
  };

  // Fetch suggestions for end
  const handleEndChange = async (e) => {
    const value = e.target.value;
    setEnd(value);
    if (value.length > 2) {
      const suggestions = await autocompletePlace(value);
      setEndSuggestions(suggestions);
    } else {
      setEndSuggestions([]);
    }
  };

  const fetchRouteHandler = async () => {
    if (!start || !end) return alert("Enter start and end locations");
    setLoading(true);
    try {
      const data = await getRoute(start, end, mode);
      setRouteData(data);
    } catch (err) {
      alert("Failed to fetch route. Check console.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="sidebar">
      <input
        type="text"
        placeholder="Start location"
        value={start}
        onChange={handleStartChange}
      />
      {startSuggestions.length > 0 && (
        <div className="suggestions">
          {startSuggestions.map((s, i) => (
            <div
              key={i}
              onClick={() => {
                setStart(s);
                setStartSuggestions([]);
              }}
            >
              {s}
            </div>
          ))}
        </div>
      )}

      <input
        type="text"
        placeholder="End location"
        value={end}
        onChange={handleEndChange}
      />
      {endSuggestions.length > 0 && (
        <div className="suggestions">
          {endSuggestions.map((s, i) => (
            <div
              key={i}
              onClick={() => {
                setEnd(s);
                setEndSuggestions([]);
              }}
            >
              {s}
            </div>
          ))}
        </div>
      )}

      <ModeSelector mode={mode} setMode={setMode} />

      <button onClick={fetchRouteHandler}>Get Route</button>
    </div>
  );
}

export default Sidebar;
